import React, { useEffect, useState } from 'react';

export default function RightSidebar({ models, selectedModel, selectedKeyword, onSelectModel }) {
  const [filteredModels, setFilteredModels] = useState([]);

  useEffect(() => {
    if (selectedKeyword) {
      // Filter models based on the selected keyword
      const filtered = models.filter((model) =>
        model.keywords.includes(selectedKeyword)
      );
      // Sort filtered models alphabetically
      setFilteredModels(filtered.sort((a, b) => a.name.localeCompare(b.name)));
    } else {
      // Sort all models alphabetically
      setFilteredModels(models.sort((a, b) => a.name.localeCompare(b.name)));
    }
  }, [selectedKeyword, models]);

  return (
    <div style={{ backgroundColor: '#FFFFFF', color: '#333333', padding: '1rem', height: '100%' }}>
      <h3 style={{ fontSize: '1rem', marginBottom: '0.5rem', color: '#333333' }}>
        {selectedKeyword ? `Models with "${selectedKeyword}"` : 'All Models'}
      </h3>
      {filteredModels.length > 0 ? (
        filteredModels.map((model) => (
          <div
            key={model.id}
            onClick={() => onSelectModel(model)}
            style={{
              cursor: 'pointer',
              padding: '0.5rem',
              marginBottom: '0.5rem',
              backgroundColor:
                selectedModel && selectedModel.id === model.id
                  ? '#E0E0E0' // Fill background for the selected model
                  : 'transparent', // No background for unselected models
              border:
                selectedModel && selectedModel.id === model.id
                  ? 'none' // No outline for the selected model
                  : '1px solid #D3D3D3', // Faint outline for unselected models
              borderRadius: '3px',
              color: '#333333',
              fontWeight: selectedModel && selectedModel.id === model.id ? 'bold' : 'normal',
              transition: 'background-color 0.2s ease, border-color 0.2s ease',
            }}
          >
            {model.name}
          </div>
        ))
      ) : (
        <p style={{ color: '#333333' }}>No models found.</p>
      )}
    </div>
  );
}
