import React, { useState, useEffect } from 'react';

export default function LeftSidebar({ models, onSelectModel }) {
  const [keywords, setKeywords] = useState({});
  const [openKeyword, setOpenKeyword] = useState(null);

  useEffect(() => {
    // Extract and organize keywords from all models
    const keywordMap = {};
    models.forEach((model) => {
      model.keywords.forEach((keyword) => {
        if (!keywordMap[keyword]) {
          keywordMap[keyword] = [];
        }
        keywordMap[keyword].push(model);
      });
    });

    // Sort keywords alphabetically
    const sortedKeywords = Object.keys(keywordMap).sort();
    const sortedKeywordMap = {};
    sortedKeywords.forEach((keyword) => {
      sortedKeywordMap[keyword] = keywordMap[keyword];
    });

    setKeywords(sortedKeywordMap);
  }, [models]);

  const handleKeywordClick = (keyword) => {
    setOpenKeyword(openKeyword === keyword ? null : keyword); // Toggle dropdown
  };

  return (
    <div style={{ color: '#ccc', fontSize: '0.9rem' }}>
      <h3 style={{ color: '#ccc', fontSize: '1rem', marginBottom: '0.5rem' }}>Keywords</h3>
      {Object.keys(keywords).map((keyword) => (
        <div key={keyword} style={{ marginBottom: '0.5rem' }}>
          <div
            onClick={() => handleKeywordClick(keyword)}
            style={{
              cursor: 'pointer',
              padding: '0.5rem',
              backgroundColor: '#024731',
              color: '#B2B2B2',
              borderRadius: '3px',
            }}
          >
            {keyword}
          </div>
          {openKeyword === keyword && (
            <div style={{ marginLeft: '1rem', marginTop: '0.5rem' }}>
              {keywords[keyword].map((model) => (
                <div
                  key={model.id}
                  onClick={() => onSelectModel(model)}
                  style={{
                    cursor: 'pointer',
                    padding: '0.3rem',
                    backgroundColor: '#03654c',
                    color: '#ccc',
                    marginBottom: '0.3rem',
                    borderRadius: '3px',
                  }}
                >
                  {model.name}
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
}
