import React, { useEffect, useState } from 'react';

export default function RightSidebar({ models, selectedModel, selectedKeyword, onSelectModel }) {
  const [sortedModels, setSortedModels] = useState([]);

  useEffect(() => {
    const sorted = models.sort((a, b) => a.name.localeCompare(b.name));
    if (selectedModel) {
      const selectedIndex = sorted.findIndex((model) => model.id === selectedModel.id);
      if (selectedIndex !== -1) {
        const selected = sorted.splice(selectedIndex, 1)[0];
        setSortedModels([selected, ...sorted]);
      } else {
        setSortedModels(sorted);
      }
    } else {
      setSortedModels(sorted);
    }
  }, [models, selectedModel]);

  return (
    <div className="sidebar">
      <h3 className="sidebar-title">
        Frameworks
      </h3>
      {sortedModels.map((model) => (
        <div
          key={model.id}
          className={`framework-item ${
            selectedModel && selectedModel.id === model.id ? 'selected-framework' : ''
          }`}
          onClick={() => onSelectModel(model)}
        >
          {model.name}
        </div>
      ))}
    </div>
  );
}
