import React from 'react';

export default function Layout({ header, leftSidebar, main, rightSidebar, footer }) {
  return (
    <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh', backgroundColor: '#FFFFFF' }}>
      <header style={{ padding: '1rem', color: '#333333', backgroundColor: '#FFFFFF', textAlign: 'center' }}>
        {header}
      </header>
      <div style={{ display: 'flex', flex: 1 }}>
        <aside
          style={{
            flex: '0 0 20%', // Left sidebar width
            padding: '1rem',
            backgroundColor: '#FFFFFF',
            overflowY: 'auto',
          }}
        >
          {leftSidebar}
        </aside>
        <main
          style={{
            flex: '1 1 60%', // Main column width
            padding: '1rem',
            backgroundColor: '#FFFFFF',
          }}
        >
          {main}
        </main>
        <aside
          style={{
            flex: '0 0 20%', // Right sidebar width
            padding: '1rem',
            backgroundColor: '#FFFFFF',
            overflowY: 'auto',
          }}
        >
          {rightSidebar}
        </aside>
      </div>
      <footer style={{ padding: '1rem', color: '#333333', backgroundColor: '#FFFFFF', textAlign: 'center' }}>
        {footer}
      </footer>
    </div>
  );
}
