import React, { useState } from 'react';
import Layout from './components/Layout';
import LeftSidebar from './components/LeftSidebar';
import RightSidebar from './components/RightSidebar';
import Home from './pages/Home';
import allModels from './data/models.json';
import categories from './data/categories.json';

function App() {
  const [selectedModel, setSelectedModel] = useState(null);
  const [selectedCategoryColor, setSelectedCategoryColor] = useState('#F5F5F5'); // Default background color

  // Function to update the category color from the left sidebar
  const handleCategoryColorChange = (color) => {
    setSelectedCategoryColor(color);
  };

  return (
    <Layout
      header={<h1 style={{ color: '#333333' }}>Conceptual Models Explorer</h1>}
      leftSidebar={
        <LeftSidebar
          models={allModels}
          categories={categories}
          selectedModel={selectedModel}
          onCategoryColorChange={handleCategoryColorChange} // Pass handler to update color
        />
      }
      main={
        <Home
          selectedModel={selectedModel}
          onSelectModel={setSelectedModel}
        />
      }
      rightSidebar={
        <RightSidebar
          models={allModels}
          selectedModel={selectedModel}
          selectedCategoryColor={selectedCategoryColor} // Pass the selected category color
          onSelectModel={setSelectedModel}
        />
      }
      footer={
        <footer>
          <p style={{ color: '#333333' }}>
            This website is solely for academic and instructional purposes for
            students. All information presented on this website belongs to the
            respective authors and/or publishers. All information should be
            accessed through official library portals. Portions of the website
            content were generated using an artificial intelligence-driven large
            language model (ChatGPT) and may contain inaccuracies; users are
            responsible for verifying the accuracy of this information against
            original sources.
          </p>
        </footer>
      }
    />
  );
}

export default App;
