import React, { useEffect, useState } from 'react';

export default function RightSidebar({ models, selectedModel, onSelectModel }) {
  const [sortedModels, setSortedModels] = useState([]);

  useEffect(() => {
    // Sort models alphabetically
    const sorted = [...models].sort((a, b) => a.name.localeCompare(b.name));
    setSortedModels(sorted);
  }, [models]);

  return (
    <div style={{ backgroundColor: '#000000', color: '#ccc', padding: '1rem' }}>
      <h3 style={{ fontSize: '1rem', marginBottom: '0.5rem', color: '#ccc' }}>
        All Models
      </h3>
      {sortedModels.length > 0 ? (
        sortedModels.map((model) => (
          <div
            key={model.id}
            onClick={() => onSelectModel(model)}
            style={{
              cursor: 'pointer',
              padding: '0.5rem',
              marginBottom: '0.5rem',
              backgroundColor:
                selectedModel && selectedModel.id === model.id
                  ? '#03654c' // Highlighted background color for selected model
                  : '#024731',
              color: selectedModel && selectedModel.id === model.id
                ? '#FFFFFF' // Highlighted text color
                : '#B2B2B2',
              borderRadius: '3px',
              fontWeight:
                selectedModel && selectedModel.id === model.id ? 'bold' : 'normal',
            }}
          >
            {model.name}
          </div>
        ))
      ) : (
        <p style={{ color: '#ccc' }}>No models loaded.</p>
      )}
    </div>
  );
}
