import React, { useEffect, useState, useRef } from 'react';

export default function RightSidebar({ models, onSelectModel }) {
  const [sortedModels, setSortedModels] = useState([]);
  const [fontSize, setFontSize] = useState('1rem'); // Default font size
  const sidebarRef = useRef(null);

  useEffect(() => {
    // Sort models alphabetically
    const sorted = [...models].sort((a, b) => a.name.localeCompare(b.name));
    setSortedModels(sorted);
  }, [models]);

  useEffect(() => {
    const handleResize = () => {
      if (sidebarRef.current) {
        const width = sidebarRef.current.offsetWidth;
        if (width < 300) {
          setFontSize('0.8rem');
        } else if (width < 400) {
          setFontSize('0.9rem');
        } else {
          setFontSize('1rem');
        }
      }
    };

    window.addEventListener('resize', handleResize);
    handleResize(); // Trigger on mount

    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <div
      ref={sidebarRef}
      style={{ backgroundColor: '#024731', color: '#ccc', padding: '1rem' }}
    >
      <h3 style={{ fontSize: fontSize, marginBottom: '0.5rem' }}>All Models</h3>
      {sortedModels.length > 0 ? (
        sortedModels.map((model) => (
          <div
            key={model.id}
            onClick={() => onSelectModel(model)}
            style={{
              cursor: 'pointer',
              padding: '0.5rem',
              marginBottom: '0.5rem',
              backgroundColor: '#03654c', // Slightly lighter green
              color: '#ccc',
              borderRadius: '3px',
              fontSize: fontSize,
            }}
            title={model.name} // Tooltip for full title
          >
            {model.name}
          </div>
        ))
      ) : (
        <p>No models loaded.</p>
      )}
    </div>
  );
}
