import React, { useState, useEffect } from 'react';

export default function LeftSidebar({ models, selectedModel }) {
  const [supercategories, setSupercategories] = useState({});
  const [openSupercategory, setOpenSupercategory] = useState(null);

  useEffect(() => {
    // Group keywords into categories, allowing overlaps
    const categories = {};
    models.forEach((model) => {
      model.keywords.forEach(({ keyword, categories: keywordCategories }) => {
        keywordCategories.forEach((category) => {
          if (!categories[category]) {
            categories[category] = [];
          }
          const existingKeyword = categories[category].find((item) => item.keyword === keyword);
          if (!existingKeyword) {
            categories[category].push({ keyword, models: [model] });
          } else {
            existingKeyword.models.push(model);
          }
        });
      });
    });
    setSupercategories(categories);
  }, [models]);

  const handleSupercategoryClick = (category) => {
    setOpenSupercategory(openSupercategory === category ? null : category);
  };

  return (
    <div style={{ color: '#ccc', fontSize: '0.9rem' }}>
      <h3 style={{ color: '#ccc', fontSize: '1rem', marginBottom: '0.5rem' }}>Categories</h3>
      {Object.keys(supercategories).map((category) => (
        <div key={category} style={{ marginBottom: '0.5rem' }}>
          <div
            onClick={() => handleSupercategoryClick(category)}
            style={{
              cursor: 'pointer',
              padding: '0.5rem',
              backgroundColor: '#024731',
              color: '#B2B2B2',
              borderRadius: '3px',
              fontWeight: openSupercategory === category ? 'bold' : 'normal',
            }}
          >
            {category}
          </div>
          {openSupercategory === category && (
            <div style={{ marginLeft: '1rem', marginTop: '0.5rem' }}>
              {supercategories[category].map(({ keyword, models }) => (
                <div
                  key={keyword}
                  style={{
                    marginBottom: '0.3rem',
                    fontWeight:
                      selectedModel && models.some((m) => m.id === selectedModel.id)
                        ? 'bold'
                        : 'normal',
                  }}
                >
                  {keyword}
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
}
