import React, { useState } from 'react';
import models from './data/models.json';
import categories from './data/categories.json';
import LeftSidebar from './components/LeftSidebar';
import RightSidebar from './components/RightSidebar';
import Layout from './components/Layout';

export default function App() {
  const defaultModel = models.find((model) => model.name === 'Health Belief Model (HBM)');
  const [selectedModel, setSelectedModel] = useState(defaultModel);
  const [selectedKeyword, setSelectedKeyword] = useState(null);

  const handleModelSelect = (model) => {
    setSelectedModel(model);
    setSelectedKeyword(null);
  };

  const handleKeywordClick = (keyword) => {
    setSelectedKeyword(keyword);
    setSelectedModel(null);
  };

  return (
    <Layout
      header={
        <div style={{ textAlign: 'center', marginBottom: '1rem' }}>
          <h1 className="header-title">Framework Finder</h1>
          <p className="header-subtitle">
            Explore various conceptual frameworks used in public health and social work research.
          </p>
        </div>
      }
      leftSidebar={
        <LeftSidebar
          categories={categories}
          selectedModel={selectedModel}
          selectedKeyword={selectedKeyword}
          onKeywordClick={handleKeywordClick}
        />
      }
      main={
        <div className="main-content">
          {selectedModel ? (
            <>
              <h2 className="model-title">{selectedModel.name}</h2>
              <table className="model-table">
                <tbody>
                  <tr>
                    <td className="table-label">Description</td>
                    <td className="table-value">{selectedModel.description}</td>
                  </tr>
                  <tr>
                    <td className="table-label">Citation</td>
                    <td className="table-value">{selectedModel.citation}</td>
                  </tr>
                  <tr>
                    <td className="table-label">Reference</td>
                    <td className="table-value">{selectedModel.reference}</td>
                  </tr>
                  <tr>
                    <td className="table-label">Source</td>
                    <td className="table-value">
                      <a href={selectedModel.url} target="_blank" rel="noopener noreferrer">
                        {selectedModel.url}
                      </a>
                    </td>
                  </tr>
                  <tr>
                    <td className="table-label">Keywords</td>
                    <td className="table-value">{selectedModel.keywords.join(', ')}</td>
                  </tr>
                </tbody>
              </table>
            </>
          ) : (
            <p className="main-placeholder">
              {selectedKeyword
                ? `Frameworks related to "${selectedKeyword}" are displayed on the right.`
                : 'Please select a model or keyword to explore details.'}
            </p>
          )}
        </div>
      }
      rightSidebar={
        <RightSidebar
          models={models}
          selectedModel={selectedModel}
          selectedKeyword={selectedKeyword}
          onSelectModel={handleModelSelect}
        />
      }
      footer={
        <p className="footer-text">
          This website is solely for instructional and academic research purposes. All information 
          presented on this website belongs to the respective authors and/or publishers. Source content 
          was gathered using an artificial intelligence-driven large language model (ChatGPT 4o) and may 
          contain inaccuracies. Students and researchers are responsible for verifying the accuracy of this 
          information against original sources.
        </p>
      }
    />
  );
}
