import React, { useEffect, useState } from 'react';

export default function RightSidebar({ models, onSelectModel }) {
  const [sortedModels, setSortedModels] = useState([]);

  useEffect(() => {
    // Sort the models alphabetically by name
    const sorted = [...models].sort((a, b) => a.name.localeCompare(b.name));
    setSortedModels(sorted);
  }, [models]);

  return (
    <div style={{ fontSize: '0.8rem', lineHeight: '1.5', overflowY: 'auto', height: '90%' }}>
      <h3 style={{ fontSize: '1rem', marginBottom: '0.5rem' }}>All Models</h3>
      {sortedModels.length > 0 ? (
        sortedModels.map((model) => (
          <div
            key={model.id}
            onClick={() => onSelectModel(model)}
            style={{
              cursor: 'pointer',
              padding: '0.2rem 0.4rem',
              marginBottom: '0.2rem',
              background: '#024731',
              color: '#ccc',
              borderRadius: '3px',
              textOverflow: 'ellipsis',
              whiteSpace: 'nowrap',
              overflow: 'hidden',
            }}
            title={model.name} // Tooltip for full name
          >
            {model.name}
          </div>
        ))
      ) : (
        <p style={{ color: '#ccc' }}>No models loaded.</p>
      )}
    </div>
  );
}
