import React, { useEffect, useState } from 'react';

export default function RightSidebar({ models, selectedModel, selectedKeyword, onSelectModel }) {
  const [filteredModels, setFilteredModels] = useState([]);

  useEffect(() => {
    if (selectedKeyword) {
      setFilteredModels(
        models.filter((model) => model.keywords.includes(selectedKeyword)).sort((a, b) => a.name.localeCompare(b.name))
      );
    } else {
      setFilteredModels(models.sort((a, b) => a.name.localeCompare(b.name)));
    }
  }, [selectedKeyword, models]);

  return (
    <div className="sidebar">
      <h3 className="sidebar-title">
        {selectedKeyword ? `Frameworks with "${selectedKeyword}"` : 'All Frameworks'}
      </h3>
      {filteredModels.map((model) => (
        <div
          key={model.id}
          className={`framework-item ${
            selectedModel?.id === model.id ? 'selected-framework' : ''
          }`}
          onClick={() => onSelectModel(model)}
        >
          {model.name}
        </div>
      ))}
    </div>
  );
}
