import React, { useEffect, useState } from 'react';

export default function RightSidebar({ models, selectedModel, selectedKeyword, onSelectModel }) {
  const [filteredModels, setFilteredModels] = useState([]);

  useEffect(() => {
    if (selectedKeyword) {
      const filtered = models.filter((model) =>
        model.keywords.includes(selectedKeyword)
      );
      setFilteredModels(filtered.sort((a, b) => a.name.localeCompare(b.name)));
    } else {
      setFilteredModels(models.sort((a, b) => a.name.localeCompare(b.name)));
    }
  }, [selectedKeyword, models]);

  return (
    <div
      style={{
        backgroundColor: '#FFFFFF',
        color: '#333333',
        padding: '1rem',
        height: '100%',
        fontFamily: 'Fira Sans Extra Condensed, sans-serif',
        fontWeight: '300', // Light weight by default
      }}
    >
      <h3
        style={{
          fontFamily: 'Montserrat, sans-serif',
          fontWeight: '600', // Semibold for titles
          fontSize: '1rem',
          marginBottom: '0.5rem',
          color: '#333333',
        }}
      >
        {selectedKeyword ? `Frameworks with "${selectedKeyword}"` : 'All Frameworks'}
      </h3>
      {filteredModels.length > 0 ? (
        filteredModels.map((model) => (
          <div
            key={model.id}
            onClick={() => onSelectModel(model)}
            style={{
              cursor: 'pointer',
              padding: '0.5rem',
              marginBottom: '0.5rem',
              backgroundColor:
                selectedModel && selectedModel.id === model.id
                  ? '#E0E0E0'
                  : 'transparent',
              border: '1px solid #D3D3D3',
              borderRadius: '3px',
              fontFamily: 'Fira Sans Extra Condensed, sans-serif',
              fontWeight:
                selectedModel && selectedModel.id === model.id ? '500' : '300', // Medium for selected
              color: '#333333',
              transition: 'background-color 0.2s ease, border-color 0.2s ease',
            }}
          >
            {model.name}
          </div>
        ))
      ) : (
        <p style={{ color: '#333333', fontFamily: 'Fira Sans Extra Condensed, sans-serif' }}>
          No frameworks found.
        </p>
      )}
    </div>
  );
}
