import React from 'react';
import './Sidebar.css';

export default function LeftSidebar({ models, selectedModel, onKeywordClick, sidebarTitle }) {
  // Extract unique keywords from all models
  const keywords = Array.from(
    new Set(models.flatMap((model) => model.keywords))
  ).sort();

  return (
    <div className="sidebar left-sidebar">
      <h3 className="sidebar-title">{sidebarTitle}</h3>
      <ul className="sidebar-list">
        {keywords.map((keyword) => (
          <li
            key={keyword}
            className={`sidebar-item ${
              selectedModel?.keywords.includes(keyword) ? 'selected' : ''
            }`}
            onClick={() => onKeywordClick(keyword)}
          >
            {keyword}
          </li>
        ))}
      </ul>
    </div>
  );
}
