import React from 'react';

export default function Layout({ header, leftSidebar, main, rightSidebar, footer }) {
  return (
    <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh', backgroundColor: '#FFFFFF' }}>
      <header style={{ padding: '1rem', color: '#333333', backgroundColor: '#FFFFFF' }}>
        {header}
      </header>
      <div style={{ display: 'flex', flex: 1 }}>
        <aside
          style={{
            flex: '0 0 20%', // Left sidebar width
            padding: '1rem',
            backgroundColor: 'white',
            color: '#333333',
          }}
        >
          {leftSidebar}
        </aside>
        <div style={{ flex: '1 1 60%', padding: '1rem', backgroundColor: '#FFFFFF', color: '#333333' }}>
          {main}
          <footer style={{ marginTop: '2rem', padding: '1rem', borderTop: '1px solid #D3D3D3', color: '#333333' }}>
            {footer}
          </footer>
        </div>
        <aside
          style={{
            flex: '0 0 20%', // Right sidebar width
            padding: '1rem',
            backgroundColor: 'white',
            color: '#333333',
          }}
        >
          {rightSidebar}
        </aside>
      </div>
    </div>
  );
}
