import React, { useState, useEffect } from 'react';
import categories from '../data/categories.json';

export default function LeftSidebar({ models, selectedModel, onKeywordClick }) {
  const [supercategories, setSupercategories] = useState({});
  const [expandedCategories, setExpandedCategories] = useState([]);
  const [selectedKeyword, setSelectedKeyword] = useState(null);

  useEffect(() => {
    const categoryMap = {};

    Object.keys(categories).forEach((keyword) => {
      const relatedModels = models.filter((model) =>
        model.keywords.includes(keyword)
      );

      categories[keyword].forEach((supercategory) => {
        if (!categoryMap[supercategory]) {
          categoryMap[supercategory] = [];
        }

        const existingKeyword = categoryMap[supercategory].find(
          (item) => item.keyword === keyword
        );

        if (!existingKeyword) {
          categoryMap[supercategory].push({ keyword, models: relatedModels });
        }
      });
    });

    // Sort categories alphabetically
    const sortedCategoryMap = {};
    Object.keys(categoryMap)
      .sort((a, b) => a.localeCompare(b))
      .forEach((key) => {
        sortedCategoryMap[key] = categoryMap[key].sort((a, b) =>
          a.keyword.localeCompare(b.keyword)
        );
      });

    setSupercategories(sortedCategoryMap);
  }, [models]);

  useEffect(() => {
    if (selectedKeyword) {
      const relevantCategory = Object.keys(supercategories).find((category) =>
        supercategories[category].some((item) => item.keyword === selectedKeyword)
      );
      setExpandedCategories(relevantCategory ? [relevantCategory] : []);
    } else if (selectedModel) {
      const relevantCategories = Object.keys(supercategories).filter((category) =>
        supercategories[category].some((item) =>
          selectedModel.keywords.includes(item.keyword)
        )
      );
      setExpandedCategories(relevantCategories);
    }
  }, [selectedModel, selectedKeyword, supercategories]);

  const toggleCategory = (category) => {
    setExpandedCategories((prev) =>
      prev.includes(category)
        ? prev.filter((c) => c !== category)
        : [...prev, category]
    );
  };

  const handleKeywordClick = (keyword, category) => {
    setSelectedKeyword(keyword);
    setExpandedCategories([category]);
    onKeywordClick(keyword);
  };

  return (
    <div
      style={{
        backgroundColor: '#FFFFFF',
        color: '#333333',
        padding: '1rem',
        height: '100%',
        fontFamily: 'Fira Sans Extra Condensed, sans-serif',
        fontWeight: '300',
      }}
    >
      <h3
        style={{
          fontFamily: 'Montserrat, sans-serif',
          fontWeight: '600',
          color: '#333333',
          fontSize: '1rem',
          marginBottom: '0.5rem',
        }}
      >
        Keywords
      </h3>
      {Object.keys(supercategories).map((category) => (
        <div key={category} style={{ marginBottom: '0.5rem' }}>
          <div
            onClick={() => toggleCategory(category)}
            style={{
              cursor: 'pointer',
              padding: '0.5rem',
              color: '#333333',
              backgroundColor: expandedCategories.includes(category)
                ? '#E0E0E0'
                : 'transparent',
              borderRadius: '3px',
              border: '1px solid #D3D3D3',
              fontWeight: expandedCategories.includes(category) ? '500' : '300',
              fontFamily: 'Fira Sans Extra Condensed, sans-serif',
              transition: 'background-color 0.2s ease, border-color 0.2s ease',
            }}
          >
            {category}
          </div>
          {expandedCategories.includes(category) && (
            <div style={{ marginLeft: '1rem', marginTop: '0.5rem' }}>
              {supercategories[category].map(({ keyword, models }) => (
                <div
                  key={keyword}
                  style={{
                    marginBottom: '0.3rem',
                    padding: '0.3rem',
                    backgroundColor:
                      selectedKeyword === keyword ? '#E0E0E0' : 'transparent',
                    borderRadius: '3px',
                    border: '1px solid #D3D3D3',
                    fontWeight: selectedKeyword === keyword ? '500' : '300',
                    fontFamily: 'Fira Sans Extra Condensed, sans-serif',
                  }}
                >
                  <a
                    href="#"
                    onClick={(e) => {
                      e.preventDefault();
                      handleKeywordClick(keyword, category);
                    }}
                    style={{
                      color: '#333333',
                      textDecoration: 'none',
                      cursor: 'pointer',
                      fontFamily: 'Fira Sans Extra Condensed, sans-serif',
                    }}
                  >
                    {keyword}
                  </a>
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
}
