import React, { useState } from 'react';
import models from './data/models.json';
import LeftSidebar from './components/LeftSidebar';
import RightSidebar from './components/RightSidebar';
import Layout from './components/Layout';
import './App.css';

export default function App() {
  const defaultModel = models.find((model) => model.name === 'Health Belief Model (HBM)');
  const [selectedModel, setSelectedModel] = useState(defaultModel);
  const [selectedKeyword, setSelectedKeyword] = useState(null);

  const handleModelSelect = (model) => {
    setSelectedModel(model);
    setSelectedKeyword(null);
  };

  const handleKeywordClick = (keyword) => {
    setSelectedKeyword(keyword);
    setSelectedModel(null);
  };

  return (
    <Layout
      header={
        <div className="header-container">
          <h1 className="header-title">Framework Finder</h1>
          <p className="header-subtitle">
            Explore various conceptual frameworks used in public health and social work research.
          </p>
        </div>
      }
      leftSidebar={
        <LeftSidebar
          models={models}
          selectedModel={selectedModel}
          onKeywordClick={handleKeywordClick}
          sidebarTitle="Keywords"
        />
      }
      main={
        <div className="main-content">
          {selectedModel ? (
            <>
              <h2 className="main-title">{selectedModel.name}</h2>
              <div className="main-image-container">
                <img
                  src={selectedModel.image}
                  alt={selectedModel.name}
                  className="main-image"
                />
              </div>
              <table className="main-table">
                <tbody>
                  <tr>
                    <td className="table-header">Description</td>
                    <td className="table-data">{selectedModel.description}</td>
                  </tr>
                  <tr>
                    <td className="table-header">Citation</td>
                    <td className="table-data">{selectedModel.citation}</td>
                  </tr>
                  <tr>
                    <td className="table-header">Reference</td>
                    <td className="table-data">{selectedModel.reference}</td>
                  </tr>
                  <tr>
                    <td className="table-header">Source</td>
                    <td className="table-data">
                      <a
                        href={selectedModel.url}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="main-link"
                      >
                        {selectedModel.url}
                      </a>
                    </td>
                  </tr>
                  <tr>
                    <td className="table-header">Keywords</td>
                    <td className="table-data">{selectedModel.keywords.join(', ')}</td>
                  </tr>
                </tbody>
              </table>
            </>
          ) : (
            <p className="main-placeholder">Please select a model to view its details.</p>
          )}
        </div>
      }
      rightSidebar={
        <RightSidebar
          models={models}
          selectedModel={selectedModel}
          selectedKeyword={selectedKeyword}
          onSelectModel={handleModelSelect}
          sidebarTitle="Frameworks"
        />
      }
      footer={
        <p className="footer-text">
          This website is solely for instructional and academic research purposes. All information 
          presented on this website belongs to the respective authors and/or publishers. Source content 
          was gathered using an artificial intelligence-driven large language model (ChatGPT 4o) and may 
          contain inaccuracies. Students and researchers are responsible for verifying the 
          accuracy of this information against original sources.
        </p>
      }
    />
  );
}
